package ex5_Thread;

public class ThreadCount implements Runnable {
//	??©¦??? ?????? ??????? ????? ??©ö??
//	???????? ??©ö??? ????? 1?? ???????? 0?? ????? ??
//	"????"??? ??????? ??? ?????? ???????????? ???????
	
	int n;
	
	public ThreadCount(int n) {
		this.n = n;
	}
	
	@Override
	public void run() {
		for (int i = n; i >= 0; i--) {
			try {
				System.out.println(i);
				Thread.sleep(1000);
			} catch (InterruptedException e) {
				// TODO Auto-generated catch block
				e.printStackTrace();
			}
		}
		System.out.println("????");
	}
}
